mapper.select:
select * from ( select A.*, rownum as RNUM from ( select no, name, pwd, title, email, regdate, readcount, content, (sysdate-regdate)*24 as new_Img_Term from board
where ${searchCondition} like '%' || #{searchKeyword} || '%'
order by no desc ) A ) where RNUM>#{firstRecordIndex} and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update board 
set readcount=readcount+1 
where no=#{no}

update board
set name=#{name}, title=#{title}, email=#{email}, content=#{content} 
where no=#{no}
